import React, { useState, useEffect } from 'react';
import './App.css';
import Home from './Pages/Home/home';
import Heritage from './Pages/Heritage/heritage'
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';

function App() {
  const [state, setState] = useState(0); // Default state is 0
  const location = useLocation();

  // Update state based on the current page (URL)
  useEffect(() => {
    if (location.pathname === '/') {
      setState(1); // Main page (Home)
    } else {
      setState(0); // Other pages
    }
  }, [location.pathname]); // Re-run whenever the URL changes
  return (
    <div className="App">
        <Router>
            <Routes>
                <Route path="/" element={<Navigate to="/home" />} /> {/* Default redirect to sign-up */}
                <Route path="/home" element={<Home />} />
                <Route path="/heritage" element={<Heritage />} />
            </Routes>
        </Router>
    </div>
  );
}

export default App;
